package com.team2.admin;

public class Reasone {

	String r_id ;
	String r_pw ;
	String r_text ;
	String r_date ;
	public Reasone() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Reasone(String r_id, String r_pw, String r_text, String r_date) {
		super();
		this.r_id = r_id;
		this.r_pw = r_pw;
		this.r_text = r_text;
		this.r_date = r_date;
	}
	public String getR_id() {
		return r_id;
	}
	public void setR_id(String r_id) {
		this.r_id = r_id;
	}
	public String getR_pw() {
		return r_pw;
	}
	public void setR_pw(String r_pw) {
		this.r_pw = r_pw;
	}
	public String getR_text() {
		return r_text;
	}
	public void setR_text(String r_text) {
		this.r_text = r_text;
	}
	public String getR_date() {
		return r_date;
	}
	public void setR_date(String r_date) {
		this.r_date = r_date;
	}

	
	
}
