package com.team2.m3;

public class Food {
private String name;
private String img;

public Food() {
	// TODO Auto-generated constructor stub
}

public Food(String name, String img) {
	super();
	this.name = name;
	this.img = img;
}

public String getName() {
	return name;
}

public void setName(String name) {
	this.name = name;
}

public String getImg() {
	return img;
}

public void setImg(String img) {
	this.img = img;
}

}
